import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';


export interface Product {
  id :number;
  name: string;
  price: number;
  amount: number;
  c: any;
} 
@Injectable({
  providedIn: 'root'
})
export class CartService {
  data: Product[] = [
    { id: 1,c: "https://secure.ap-tescoassets.com/assets/TH/559/8858998581559/ShotType1_540x540.jpg", name: 'Pepsi', price: 12, amount: 1 },
    { id: 2,c: "data:image/jpeg;base64,", name: 'น้ำแดง แฟนต้า', price:  15, amount: 1 },
    { id: 3,c: "data:image/jpeg;base64,", name: 'น้ำเขียว แฟนต้า', price: 15, amount: 1 },
    { id: 4,c: "data:image/jpeg;base64,",name: 'น้ำส้ม แฟนต้า', price: 15, amount: 1 },
    { id: 5,c: "data:image/jpeg;base64,",name: 'สไปรท์', price: 15, amount: 1 },
    { id: 6,c: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSejKnQjWrufJasVRqidS_g5yg7vg3vnULwlQAqLt8OGePYRbT9ut80QoY6K7lbNyBRxP2d7_2I&usqp=CAc",name: 'เลย์ รสสาหร่าย', price: 20, amount: 1 },
    { id: 7,c: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQFCZ5KiDBO99QNO3veMcaCq2-ym5DakwyTx3lFDOYRJnMXXeCtU-1UzfRkbcou-Xn8BuPcytM&usqp=CAc",name: 'เลย์ รสดั้งเดิม ', price: 20, amount: 1 },
    { id: 8,c: "data:image/jpeg;base64,",name: 'ครัวซอง ', price: 13, amount: 1 },
    { id: 9,c: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRW18_aWk6PTUfLi8kgbh3Bsko4cznO6bYPKg&usqp=CAU",name: 'แซนวิชใส้กรอกชิส ', price: 25, amount: 1 },
    
  ];
 
  private cart = [];
  private cartItemCount = new BehaviorSubject(0);
  constructor() { }

  getProducts() {
    return this.data;
  }
  getCart() {
    return this.cart;
  }
  getCartItemCount(){
    return this.cartItemCount;
  }

  addProduct(product){
    let added = false;
    for (let p of this.cart) {
      if (p.id ===product.id) {
       
        added = true;
        break;
      }
    }
      if (!added) {
        this.cart.push(product);
      }
      this.cartItemCount.next(this.cartItemCount.value + 1);
    } 
 
  decreaseProduct(product) {
    for (let [index, p] of this.cart.entries()) {
      if (p.id === product.id) {
        p.amount -= 1;
        if (p.amount ==0) {
          this.cart.splice(index, 1);
        }
      }
    }
    this.cartItemCount.next(this.cartItemCount.value - 1);
  }

  removeProduct(product) {
    for (let [index, p] of this.cart.entries()) {
      if (p.id === product.id) {
        this.cartItemCount.next(this.cartItemCount.value - p.amount);
          this.cart.splice(index, 1);
        }
      }
    }
  
}
